  `;

  // Email to user (confirmation) - Short version to prevent clipping
  const userHtmlContent = `
    <div style="font-family: Arial, sans-serif; max-width: 500px; margin: 0 auto;">
      <h2 style="color: #2563eb;">Bedankt ${name}!</h2>
