        console.log(`Deleted: ${filePath}`);
      }

      // Return success response
      res.status(200).json({
        success: true,
        message: "Service deleted successfully"
      });
    } catch (error) {
      console.error("Error deleting service:", error);
      res.status(500).json({
        success: false,
        message: "Failed to delete service"
      });
    }
  });

  // Update service 
  app.patch("/api/services/:slug", async (req, res) => {
    try {
      const { slug } = req.params;
      const updates = req.body;
      const language = req.query.lang || 'nl';
      const contentDir = path.join(process.cwd(), 'public/content/services');

      // Construct the expected filename
      const filename = `${slug}.${language}.md`;
      const filePath = path.join(contentDir, filename);

      // Check if file exists
      try {
        await fs.access(filePath);
      } catch (err) {
        return res.status(404).json({
          success: false,
          message: "Service not found"
        });
      }

      // Read and parse the file
      const content = await fs.readFile(filePath, 'utf8');
      const parsed = matter(content);

      // Extract content if it was provided
      let markdownContent = parsed.content;
      if (updates.content !== undefined) {
        markdownContent = updates.content;
        delete updates.content; // Remove from updates to avoid storing it in the frontmatter
      }

      // Update the metadata
      Object.assign(parsed.data, updates);

      // Write the updated content back to the file with new markdown content
      const updatedContent = matter.stringify(markdownContent, parsed.data);
      await fs.writeFile(filePath, updatedContent);

      // Return success response
      res.status(200).json({
        success: true,
        message: "Service updated successfully",
        data: {
          slug,
          ...parsed.data,
          content: markdownContent
        }
      });
    } catch (error) {
      console.error("Error updating service:", error);
      res.status(500).json({
        success: false,
        message: "Failed to update service"
      });
    }
  });

  // Create new service
  app.post("/api/services", async (req, res) => {
    try {
      const { title, slug, icon, short_description, features, tech_stack, content, order } = req.body;
      const language = req.query.lang || 'nl';
      const contentDir = path.join(process.cwd(), 'public/content/services');

      // Ensure content directory exists
      try {
        await fs.mkdir(contentDir, { recursive: true });
      } catch (err) {
        // Ignore if directory already exists
      }

      // Construct the filename
      const filename = `${slug}.${language}.md`;
      const filePath = path.join(contentDir, filename);

      // Check if file already exists
      try {
        await fs.access(filePath);
        return res.status(409).json({
          success: false,
          message: "Service with this slug already exists"
        });
      } catch (err) {
        // File doesn't exist, this is what we want
      }

      // Prepare the data
      const data = {
        title: title || '',
        slug,
        icon: icon || 'code',
        short_description: short_description || '',
        featured_image: '/uploads/default-service.jpg',
        order: order || 999,
        features: features || [],
        tech_stack: tech_stack || []
      };

      // Create the file content with frontmatter
      const fileContent = matter.stringify(content || '# New Service\n\nAdd content here.', data);
      await fs.writeFile(filePath, fileContent);

      // Return success response
      res.status(201).json({
        success: true,
        message: "Service created successfully",
        data
      });
    } catch (error) {
      console.error("Error creating service:", error);
      res.status(500).json({
        success: false,
        message: "Failed to create service"
      });
    }
  });

  // Set up local CMS authentication backend
  const cmsContentRoot = path.join(process.cwd(), 'public/content');
  const cmsMediaRoot = path.join(process.cwd(), 'public/uploads');

  // Default login credentials for CMS (only in development)
  const username = 'admin';
  const password = 'digimaatwerk2024';

  // Manual login endpoint for CMS
  app.post('/api/admin/login', (req, res) => {
    try {
      const { email, password } = req.body;

      // Simple authentication for development
      if (email === username && password === password) {
        res.status(200).json({ 
          success: true, 
          token: 'mock-token', 
          email: email 
        });
      } else {
        res.status(401).json({ 
          success: false, 
          message: 'Invalid credentials' 
        });
      }
    } catch (error) {
      console.error('Login error:', error);
      res.status(500).json({ 
        success: false, 
        message: 'Internal server error' 
      });
    }
  });

  // Login route for CMS admin
  app.post('/admin/api/login', (req, res) => {
    const { email, password } = req.body;

    // We accept any credentials in the test/development environment
    // This is only for testing the CMS, in production this would use proper auth

    // Simple session-based auth
    if (!req.session) {
      return res.status(500).json({ 
        success: false, 
        message: "Session management not available" 
      });
    }

    // Set session
    req.session.user = { email, isAdmin: true };

    return res.status(200).json({
      success: true,
      message: "Login successful",
      data: {
        email: email,
        token: "mock-token" // In production, use a real JWT or similar
      }
    });
  });

  // Check if user is authenticated
  app.get('/admin/api/auth/check', (req, res) => {
    if (req.session && req.session.user) {
      return res.status(200).json({
        success: true,
        message: "Authenticated",
        data: {
          email: req.session.user.email
        }
      });
    }

    return res.status(401).json({
      success: false,
      message: "Not authenticated"
    });
  });

  // Authentication check middleware
  const requireAuth = (req: Request, res: Response, next: NextFunction) => {
    if (req.session && req.session.user) {
      return next();
    }

    return res.status(401).json({
      success: false,
      message: "Authentication required"
    });
  };

  // CMS API routes
  app.post('/admin/api/collections/:collection', requireAuth, async (req, res) => {
    try {
      const { collection } = req.params;
      const data = req.body;

      // Ensure content directory exists
      const contentDir = path.join(process.cwd(), 'public/content', collection);
      try {
        await fs.access(contentDir);
      } catch (error) {
        await fs.mkdir(contentDir, { recursive: true });
      }

      // Save the content
      const slug = data.slug || Date.now().toString();
      const language = data.language || 'nl';
      const content = matter.stringify(data.content || '', data);

      await fs.writeFile(
        path.join(contentDir, `${slug}.${language}.md`),
        content
      );

      res.status(201).json({
        success: true,
        message: `${collection} item created`,
        data: { slug, language }
      });
    } catch (error) {
      console.error(`Error saving ${req.params.collection}:`, error);
      res.status(500).json({
        success: false,
        message: "Failed to save content"
      });
    }
  });

  // Webhook management endpoints
  // Get all webhooks
  app.get("/api/webhooks", async (req, res) => {
    try {
      const webhooks = await storage.getAllWebhooks();
      res.json({
        success: true,
        message: "Webhooks retrieved successfully",
        data: webhooks
      });
    } catch (error) {
      console.error("Error fetching webhooks:", error);
      res.status(500).json({
        success: false,
        message: "Failed to retrieve webhooks"
      });
    }
  });

  // Get webhook by ID
  app.get("/api/webhooks/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      if (isNaN(id)) {
        return res.status(400).json({
          success: false,
          message: "Invalid webhook ID"
        });
      }

      const webhook = await storage.getWebhook(id);
      if (!webhook) {
        return res.status(404).json({
          success: false,
          message: "Webhook not found"
        });
      }

      res.json({
        success: true,
        message: "Webhook retrieved successfully",
        data: webhook
      });
    } catch (error) {
      console.error("Error fetching webhook:", error);
      res.status(500).json({
        success: false,
        message: "Failed to retrieve webhook"
      });
    }
  });

  // Create webhook
  app.post("/api/webhooks", async (req, res) => {
    try {
      const webhookData = {
        name: req.body.name,
        url: req.body.url,
        eventType: req.body.event_type, // Convert from request field to schema field
        secretToken: req.body.secret_token || null,
        isActive: req.body.is_active === "true" || req.body.is_active === true
      };

      const webhook = await storage.createWebhook(webhookData);
      res.status(201).json({
        success: true,
        message: "Webhook created successfully",
        data: webhook
      });
    } catch (error) {
      console.error("Error creating webhook:", error);
      res.status(500).json({
        success: false,
        message: "Failed to create webhook"
      });
    }
  });

  // Update webhook
  app.put("/api/webhooks/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      if (isNaN(id)) {
        return res.status(400).json({
          success: false,
          message: "Invalid webhook ID"
        });
      }

      const webhookData = {
        name: req.body.name,
        url: req.body.url,
        eventType: req.body.event_type, // Convert from request field to schema field
        secretToken: req.body.secret_token || null,
        isActive: req.body.is_active === "true" || req.body.is_active === true
      };

      const webhook = await storage.updateWebhook(id, webhookData);
      res.json({
        success: true,
        message: "Webhook updated successfully",
        data: webhook
      });
    } catch (error) {
      console.error("Error updating webhook:", error);
      res.status(500).json({
        success: false,
        message: "Failed to update webhook"
      });
    }
  });

  // Delete webhook
  app.delete("/api/webhooks/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      if (isNaN(id)) {
        return res.status(400).json({
          success: false,
          message: "Invalid webhook ID"
        });
      }

      await storage.deleteWebhook(id);
      res.json({
        success: true,
        message: "Webhook deleted successfully"
      });
    } catch (error) {
      console.error("Error deleting webhook:", error);
      res.status(500).json({
        success: false,
        message: "Failed to delete webhook"
      });
    }
  });

  // API Token endpoints
  // Get all API tokens
  app.get("/api/tokens", requireAuth, async (req, res) => {
    try {
      if (!req.session.user) {
        return res.status(401).json({
          success: false,
          message: "Authentication required"
        });
      }

      // In a real app, we would use the user's ID from the session
      // For now, use a placeholder user ID
      const userId = 1;

      const tokens = await storage.getAllApiTokens(userId);
      res.json({
        success: true,
        message: "API tokens retrieved successfully",
        data: tokens
      });
    } catch (error) {
      console.error("Error fetching API tokens:", error);
      res.status(500).json({
        success: false,
        message: "Failed to retrieve API tokens"
      });
    }
  });

  // Create API token
  app.post("/api/tokens", requireAuth, async (req, res) => {
    try {
      if (!req.session.user) {
        return res.status(401).json({
          success: false,
          message: "Authentication required"
        });
      }

      // In a real app, we would use the user's ID from the session
      // For now, use a placeholder user ID
      const userId = 1;

      const tokenValue = generateTokenValue();
      const tokenData = {
        userId: userId,
        name: req.body.name,
        token: tokenValue,
        scopes: req.body.scopes || ["read"],
        expiresAt: new Date(Date.now() + 365 * 24 * 60 * 60 * 1000) // 1 year
      };

      const token = await storage.createApiToken(tokenData);
      res.status(201).json({
        success: true,
        message: "API token created successfully",
        data: {
          ...token,
          plainToken: tokenValue // Only send the token value once!
        }
      });
    } catch (error) {
      console.error("Error creating API token:", error);
      res.status(500).json({
        success: false,
        message: "Failed to create API token"
      });
    }
  });

  // Delete API token
  app.delete("/api/tokens/:id", requireAuth, async (req, res) => {
    try {
      if (!req.session.user) {
        return res.status(401).json({
          success: false,
          message: "Authentication required"
        });
      }

      const id = parseInt(req.params.id);
      if (isNaN(id)) {
        return res.status(400).json({
          success: false,
          message: "Invalid token ID"
        });
      }

      await storage.deleteApiToken(id);
      res.json({
        success: true,
        message: "API token deleted successfully"
      });
    } catch (error) {
      console.error("Error deleting API token:", error);
      res.status(500).json({
        success: false,
        message: "Failed to delete API token"
      });
    }
  });

  // Helper function to generate a random token value
  function generateTokenValue() {
    return 'tk_' + [...Array(32)]
      .map(() => Math.floor(Math.random() * 16).toString(16))
      .join('');
  }

  // Cookie consent tracking endpoints
  app.post('/api/cookie-consent', async (req: Request, res: Response) => {
    try {
      const consentData = {
        ...req.body,
        ipAddress: req.ip || req.connection.remoteAddress,
        userAgent: req.get('User-Agent'),
        consentGivenAt: new Date()
      };

      const consent = await storage.createCookieConsent(consentData);

      // Track analytics event
      await storage.createCookieAnalytics({
        sessionId: consentData.sessionId,
        eventType: 'consent_given',
        eventData: {
          analytics: consentData.analytics,
          marketing: consentData.marketing,
          preferences: consentData.preferences
        },
        timestamp: new Date(),
        pageUrl: consentData.pageUrl,
        userAgent: consentData.userAgent
      });

      res.json({ success: true, data: consent });
    } catch (error) {
      console.error('Cookie consent error:', error);
      res.status(500).json({ error: 'Failed to save cookie consent' });
    }
  });

  app.post('/api/cookie-analytics', async (req: Request, res: Response) => {
    try {
      const analyticsData = {
        ...req.body,
        timestamp: new Date(),
        userAgent: req.get('User-Agent')
      };

      const analytics = await storage.createCookieAnalytics(analyticsData);
      res.json({ success: true, data: analytics });
    } catch (error) {
      console.error('Cookie analytics error:', error);
      res.status(500).json({ error: 'Failed to save cookie analytics' });
    }
  });

  // Admin endpoints for cookie tracking dashboard
  app.get('/api/admin/cookie-stats', async (req: Request, res: Response) => {
    try {
      const { startDate, endDate } = req.query;

      const start = startDate ? new Date(startDate as string) : undefined;
      const end = endDate ? new Date(endDate as string) : undefined;

      const stats = await storage.getCookieConsentStats(start, end);
      const analyticsStats = await storage.getCookieAnalyticsStats(start, end);

      res.json({
        success: true,
        data: {
          consent: stats,
          analytics: analyticsStats
        }
      });
    } catch (error) {
      console.error('Cookie stats error:', error);
      res.status(500).json({ error: 'Failed to get cookie statistics' });
    }
  });

  // GDPR request endpoints
  app.post('/api/gdpr-request', async (req: Request, res: Response) => {
    try {
      const requestData = {
        ...req.body,
        submittedAt: new Date()
      };

      const gdprRequest = await storage.createGdprRequest(requestData);
      res.json({ success: true, data: gdprRequest });
    } catch (error) {
      console.error('GDPR request error:', error);
      res.status(500).json({ error: 'Failed to submit GDPR request' });
    }
  });

  app.get('/api/admin/gdpr-requests', async (req: Request, res: Response) => {
    try {
      const requests = await storage.getAllGdprRequests();
      res.json({ success: true, data: requests });
    } catch (error) {
      console.error('Get GDPR requests error:', error);
      res.status(500).json({ error: 'Failed to get GDPR requests' });
    }
  });

  // Test email endpoint
  app.post("/api/test-email", async (req, res) => {
    try {
      await sendContactEmail({
        name: "Test User",
        email: "dckessler95@gmail.com",
        company: "Test Company",
        projectType: "Web Development",
        message: "Dit is een test e-mail om de Strato mailserver configuratie te verifiÃ«ren."
      });

      res.json({
        success: true,
        message: "Test email sent successfully to info@digimaatwerk.nl"
      });
    } catch (error) {
      console.error('Test email failed:', error);
      res.status(500).json({
        success: false,
        message: "Failed to send test email",
        error: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  // Verify email configuration on startup
  verifyEmailConfig().then(isWorking => {
    if (isWorking) {
      console.log('âœ… Email server configuration verified successfully');
    } else {
      console.log('âŒ Email server configuration failed - check EMAIL_PASSWORD environment variable');
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, type User, type InsertUser,
  contactSubmissions, type ContactSubmission, type InsertContactSubmission,
  chatMessages, type ChatMessage, type InsertChatMessage,
  projectEstimates, type ProjectEstimate, type InsertProjectEstimate,
  webhooks, type Webhook, type InsertWebhook,
  webhookLogs, type WebhookLog, type InsertWebhookLog,
  apiTokens, type ApiToken, type InsertApiToken,
  cookieConsents, type CookieConsent, type InsertCookieConsent,
  cookieAnalytics, type CookieAnalytics, type InsertCookieAnalytics,
  gdprRequests, type GdprRequest, type InsertGdprRequest
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and } from "drizzle-orm";

// Expanded interface to support all tables
export interface IStorage {
  // User operations
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Contact submission operations
  getContactSubmission(id: number): Promise<ContactSubmission | undefined>;
  createContactSubmission(submission: InsertContactSubmission): Promise<ContactSubmission>;
  getAllContactSubmissions(): Promise<ContactSubmission[]>;
  markContactSubmissionAsProcessed(id: number): Promise<void>;

  // Chat message operations
  getChatMessage(id: number): Promise<ChatMessage | undefined>;
  createChatMessage(message: InsertChatMessage): Promise<ChatMessage>;
  getChatMessageHistory(limit?: number): Promise<ChatMessage[]>;

  // Project estimate operations
  getProjectEstimate(id: number): Promise<ProjectEstimate | undefined>;
  createProjectEstimate(estimate: InsertProjectEstimate): Promise<ProjectEstimate>;
  getAllProjectEstimates(): Promise<ProjectEstimate[]>;

  // Webhook operations
  getWebhook(id: number): Promise<Webhook | undefined>;
  createWebhook(webhook: InsertWebhook): Promise<Webhook>;
  updateWebhook(id: number, webhook: Partial<InsertWebhook>): Promise<Webhook>;
  deleteWebhook(id: number): Promise<void>;
  getAllWebhooks(): Promise<Webhook[]>;
  getWebhooksByEventType(eventType: string): Promise<Webhook[]>;

  // Webhook log operations
  createWebhookLog(log: InsertWebhookLog): Promise<WebhookLog>;
  getWebhookLogs(webhookId: number, limit?: number): Promise<WebhookLog[]>;

  // API Token operations
  getApiToken(id: number): Promise<ApiToken | undefined>;
  getApiTokenByValue(token: string): Promise<ApiToken | undefined>;
  createApiToken(token: InsertApiToken): Promise<ApiToken>;
  deleteApiToken(id: number): Promise<void>;
  getAllApiTokens(userId: number): Promise<ApiToken[]>;
  updateApiTokenLastUsed(id: number): Promise<void>;

  // Cookie consent operations
  createCookieConsent(consent: InsertCookieConsent): Promise<CookieConsent>;
  updateCookieConsent(sessionId: string, consent: Partial<InsertCookieConsent>): Promise<CookieConsent>;
  getCookieConsentBySession(sessionId: string): Promise<CookieConsent | undefined>;
  getCookieConsentStats(startDate?: Date, endDate?: Date): Promise<any>;

  // Cookie analytics operations
  createCookieAnalytics(analytics: InsertCookieAnalytics): Promise<CookieAnalytics>;
  getCookieAnalyticsBySession(sessionId: string): Promise<CookieAnalytics[]>;
  getCookieAnalyticsStats(startDate?: Date, endDate?: Date): Promise<any>;

  // GDPR request operations
  createGdprRequest(request: InsertGdprRequest): Promise<GdprRequest>;
  getGdprRequest(id: number): Promise<GdprRequest | undefined>;
  getAllGdprRequests(): Promise<GdprRequest[]>;
  updateGdprRequestStatus(id: number, status: string, processedBy?: number, responseNotes?: string): Promise<GdprRequest>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: number): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  // Contact submission operations
  async getContactSubmission(id: number): Promise<ContactSubmission | undefined> {
    const [submission] = await db.select().from(contactSubmissions).where(eq(contactSubmissions.id, id));
    return submission;
  }

  async createContactSubmission(submission: InsertContactSubmission): Promise<ContactSubmission> {
    const [contactSubmission] = await db
      .insert(contactSubmissions)
      .values(submission)
      .returning();
    return contactSubmission;
  }

  async getAllContactSubmissions(): Promise<ContactSubmission[]> {
    return await db.select().from(contactSubmissions).orderBy(contactSubmissions.submittedAt);
  }

  async markContactSubmissionAsProcessed(id: number): Promise<void> {
    await db
      .update(contactSubmissions)
      .set({ isProcessed: true })
      .where(eq(contactSubmissions.id, id));
  }

  // Chat message operations
  async getChatMessage(id: number): Promise<ChatMessage | undefined> {
    const [message] = await db.select().from(chatMessages).where(eq(chatMessages.id, id));
    return message;
  }

  async createChatMessage(message: InsertChatMessage): Promise<ChatMessage> {
    const [chatMessage] = await db
      .insert(chatMessages)
      .values(message)
      .returning();
    return chatMessage;
  }

  async getChatMessageHistory(limit: number = 50): Promise<ChatMessage[]> {
    return await db
      .select()
      .from(chatMessages)
      .orderBy(chatMessages.timestamp)
      .limit(limit);
  }

  // Project estimate operations
  async getProjectEstimate(id: number): Promise<ProjectEstimate | undefined> {
    const [estimate] = await db.select().from(projectEstimates).where(eq(projectEstimates.id, id));
    return estimate;
  }

  async createProjectEstimate(estimate: InsertProjectEstimate): Promise<ProjectEstimate> {
    const [projectEstimate] = await db
      .insert(projectEstimates)
      .values(estimate)
      .returning();
    return projectEstimate;
  }

  async getAllProjectEstimates(): Promise<ProjectEstimate[]> {
    return await db.select().from(projectEstimates).orderBy(projectEstimates.createdAt);
  }

  // Webhook operations
  async getWebhook(id: number): Promise<Webhook | undefined> {
    const [webhook] = await db.select().from(webhooks).where(eq(webhooks.id, id));
    return webhook;
  }

  async createWebhook(webhook: InsertWebhook): Promise<Webhook> {
    const [newWebhook] = await db
      .insert(webhooks)
      .values({
        ...webhook,
        createdAt: new Date()
      })
      .returning();
    return newWebhook;
  }

  async updateWebhook(id: number, webhook: Partial<InsertWebhook & { lastTriggeredAt?: Date }>): Promise<Webhook> {
    const [updatedWebhook] = await db
      .update(webhooks)
      .set(webhook)
      .where(eq(webhooks.id, id))
      .returning();
    return updatedWebhook;
  }

  async deleteWebhook(id: number): Promise<void> {
    // First delete related webhook logs
    await db.delete(webhookLogs).where(eq(webhookLogs.webhookId, id));
    // Then delete the webhook
    await db.delete(webhooks).where(eq(webhooks.id, id));
  }

  async getAllWebhooks(): Promise<Webhook[]> {
    return await db
      .select()
      .from(webhooks)
      .orderBy(webhooks.createdAt);
  }

  async getWebhooksByEventType(eventType: string): Promise<Webhook[]> {
    return await db
      .select()
      .from(webhooks)
      .where(and(
        eq(webhooks.eventType, eventType),
        eq(webhooks.isActive, true)
      ));
  }

  // Webhook log operations
  async createWebhookLog(log: InsertWebhookLog): Promise<WebhookLog> {
    const [webhookLog] = await db
      .insert(webhookLogs)
      .values({
        ...log,
        triggeredAt: new Date()
      })
      .returning();
    return webhookLog;
  }

  async getWebhookLogs(webhookId: number, limit: number = 100): Promise<WebhookLog[]> {
    return await db
      .select()
      .from(webhookLogs)
      .where(eq(webhookLogs.webhookId, webhookId))
      .orderBy(desc(webhookLogs.triggeredAt))
      .limit(limit);
  }

  // API Token operations
  async getApiToken(id: number): Promise<ApiToken | undefined> {
    const [token] = await db.select().from(apiTokens).where(eq(apiTokens.id, id));
    return token;
  }

  async getApiTokenByValue(token: string): Promise<ApiToken | undefined> {
    const [apiToken] = await db.select().from(apiTokens).where(eq(apiTokens.token, token));
    return apiToken;
  }

  async createApiToken(token: InsertApiToken): Promise<ApiToken> {
    const [newToken] = await db
      .insert(apiTokens)
      .values({
        ...token,
        createdAt: new Date()
      })
      .returning();
    return newToken;
  }

  async deleteApiToken(id: number): Promise<void> {
    await db.delete(apiTokens).where(eq(apiTokens.id, id));
  }

  async getAllApiTokens(userId: number): Promise<ApiToken[]> {
    return await db
      .select()
      .from(apiTokens)
      .where(eq(apiTokens.userId, userId))
      .orderBy(apiTokens.createdAt);
  }

  async updateApiTokenLastUsed(id: number): Promise<void> {
    await db
      .update(apiTokens)
      .set({ lastUsedAt: new Date() })
      .where(eq(apiTokens.id, id));
  }

  // Cookie consent operations
  async createCookieConsent(consent: InsertCookieConsent): Promise<CookieConsent> {
    const [cookieConsent] = await db
      .insert(cookieConsents)
      .values(consent)
      .returning();
    return cookieConsent;
  }

  async updateCookieConsent(sessionId: string, consent: Partial<InsertCookieConsent>): Promise<CookieConsent> {
    const [updated] = await db
      .update(cookieConsents)
      .set({ ...consent, consentUpdatedAt: new Date() })
      .where(eq(cookieConsents.sessionId, sessionId))
      .returning();
    return updated;
  }

  async getCookieConsentBySession(sessionId: string): Promise<CookieConsent | undefined> {
    const [consent] = await db
      .select()
      .from(cookieConsents)
      .where(eq(cookieConsents.sessionId, sessionId))
      .orderBy(desc(cookieConsents.consentGivenAt))
      .limit(1);
    return consent || undefined;
  }

  async getCookieConsentStats(startDate?: Date, endDate?: Date): Promise<any> {
    const consents = await db.select().from(cookieConsents);

    const total = consents.length;
    const analytics = consents.filter(c => c.analytics).length;
    const marketing = consents.filter(c => c.marketing).length;
    const preferences = consents.filter(c => c.preferences).length;

    return {
      total,
      analytics: { count: analytics, percentage: total > 0 ? (analytics / total) * 100 : 0 },
      marketing: { count: marketing, percentage: total > 0 ? (marketing / total) * 100 : 0 },
      preferences: { count: preferences, percentage: total > 0 ? (preferences / total) * 100 : 0 },
      dailyStats: this.groupConsentsByDate(consents)
    };
  }

  private groupConsentsByDate(consents: CookieConsent[]) {
    const grouped = consents.reduce((acc, consent) => {
      const date = consent.consentGivenAt.toISOString().split('T')[0];
      if (!acc[date]) {
        acc[date] = { total: 0, analytics: 0, marketing: 0, preferences: 0 };
      }
      acc[date].total++;
      if (consent.analytics) acc[date].analytics++;
      if (consent.marketing) acc[date].marketing++;
      if (consent.preferences) acc[date].preferences++;
      return acc;
    }, {} as Record<string, any>);

    return Object.entries(grouped).map(([date, stats]) => ({ date, ...stats }));
  }

  // Cookie analytics operations
  async createCookieAnalytics(analytics: InsertCookieAnalytics): Promise<CookieAnalytics> {
    const [cookieAnalyticsResult] = await db
      .insert(cookieAnalytics)
      .values(analytics)
      .returning();
    return cookieAnalyticsResult;
  }

  async getCookieAnalyticsBySession(sessionId: string): Promise<CookieAnalytics[]> {
    return await db
      .select()
      .from(cookieAnalytics)
      .where(eq(cookieAnalytics.sessionId, sessionId))
      .orderBy(desc(cookieAnalytics.timestamp));
  }

  async getCookieAnalyticsStats(startDate?: Date, endDate?: Date): Promise<any> {
    const events = await db.select().from(cookieAnalytics);

    const eventCounts = events.reduce((acc, event) => {
      acc[event.eventType] = (acc[event.eventType] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    return {
      totalEvents: events.length,
      eventTypes: eventCounts,
      dailyEvents: this.groupEventsByDate(events)
    };
  }

  private groupEventsByDate(events: CookieAnalytics[]) {
    const grouped = events.reduce((acc, event) => {
      const date = event.timestamp.toISOString().split('T')[0];
      if (!acc[date]) {
        acc[date] = {};
      }
      acc[date][event.eventType] = (acc[date][event.eventType] || 0) + 1;
      return acc;
    }, {} as Record<string, any>);

    return Object.entries(grouped).map(([date, events]) => ({ date, ...events }));
  }

  // GDPR request operations
  async createGdprRequest(request: InsertGdprRequest): Promise<GdprRequest> {
    const [gdprRequest] = await db
      .insert(gdprRequests)
      .values(request)
      .returning();
    return gdprRequest;
  }

  async getGdprRequest(id: number): Promise<GdprRequest | undefined> {
    const [request] = await db
      .select()
      .from(gdprRequests)
      .where(eq(gdprRequests.id, id));
    return request || undefined;
  }

  async getAllGdprRequests(): Promise<GdprRequest[]> {
    return await db
      .select()
      .from(gdprRequests)
      .orderBy(desc(gdprRequests.submittedAt));
  }

  async updateGdprRequestStatus(id: number, status: string, processedBy?: number, responseNotes?: string): Promise<GdprRequest> {
    const updateData: any = {
      status,
      processedAt: new Date()
    };

    if (processedBy) updateData.processedBy = processedBy;
    if (responseNotes) updateData.responseNotes = responseNotes;

    const [updated] = await db
      .update(gdprRequests)
      .set(updateData)
      .where(eq(gdprRequests.id, id))
      .returning();
    return updated;
  }
}

export const storage = new DatabaseStorage();

