server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path, { dirname } from "path";
import { fileURLToPath } from "url";
import { createServer as createViteServer, createLogger } from "vite";
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        __dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(__dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

server/webhooks.ts.content:
import { Request, Response, Express } from 'express';
import { storage } from './storage';
import { insertContactSubmissionSchema, insertChatMessageSchema, insertProjectEstimateSchema } from '@shared/schema';
import { z } from 'zod';
import crypto from 'crypto';

// Schema's voor webhook data die string datums converteert naar Date objecten
const webhookContactSchema = insertContactSubmissionSchema.extend({
  submittedAt: z.string().transform(str => new Date(str)),
});

const webhookChatSchema = insertChatMessageSchema.extend({
  timestamp: z.string().transform(str => new Date(str)),
});

const webhookEstimateSchema = insertProjectEstimateSchema.extend({
  createdAt: z.string().transform(str => new Date(str)),
});

const webhookSecret = process.env.WEBHOOK_SECRET_TOKEN;

// Beveiligingsmiddleware voor webhooks
const validateWebhookRequest = (req: Request, res: Response, next: Function) => {
  const token = req.headers['x-webhook-token'] as string;

  if (!webhookSecret) {
    console.error('WEBHOOK_SECRET_TOKEN environment variable not set');
    return res.status(500).json({ 
      success: false, 
      message: 'Webhook secret not configured on server' 
    });
  }

  if (!token || token !== webhookSecret) {
    return res.status(401).json({ 
      success: false, 
      message: 'Unauthorized webhook request' 
    });
  }

  next();
};

// Dynamic webhook dispatcher function
async function dispatchWebhooks(eventType: string, data: any) {
  try {
    const webhooks = await storage.getWebhooksByEventType(eventType);
    const activeWebhooks = webhooks.filter(w => w.isActive);

    if (activeWebhooks.length === 0) {
      console.log(`[Webhook] No active webhooks found for event: ${eventType}`);
      return;
    }

    console.log(`[Webhook] Dispatching to ${activeWebhooks.length} webhooks for event: ${eventType}`);

    // Send to all active webhooks for this event type
    const promises = activeWebhooks.map(async (webhook) => {
      const startTime = Date.now();

      try {
        const headers: Record<string, string> = {
          'Content-Type': 'application/json',
          'User-Agent': 'Digimaatwerk-Webhook/1.0'
        };

        if (webhook.secretToken) {
          headers['X-Webhook-Token'] = webhook.secretToken;
        }

        const payload = {
          eventType,
          timestamp: new Date().toISOString(),
          data
        };

        const response = await fetch(webhook.url, {
          method: 'POST',
          headers: headers,
          body: JSON.stringify(payload)
        });

        const duration = Date.now() - startTime;
        const responseText = await response.text();

        // Log successful webhook
        await storage.createWebhookLog({
          webhookId: webhook.id,
          eventType,
          payload,
          responseStatus: response.status,
          responseBody: responseText.substring(0, 1000),
          duration
        });

        // Update last triggered timestamp - direct database update
        await storage.updateWebhook(webhook.id, { 
          lastTriggeredAt: new Date() 
        } as any);

        console.log(`[Webhook] Success: ${webhook.name} (${response.status}) in ${duration}ms`);

      } catch (error: any) {
        const duration = Date.now() - startTime;

        // Log failed webhook
        await storage.createWebhookLog({
          webhookId: webhook.id,
          eventType,
          payload: { eventType, data },
          error: error.message,
          duration
        });

        console.error(`[Webhook] Failed: ${webhook.name} - ${error.message}`);
      }
    });

    await Promise.allSettled(promises);

  } catch (error) {
    console.error(`[Webhook] Dispatcher error for event ${eventType}:`, error);
  }
}

export const setupWebhookRoutes = (app: Express) => {
  // Legacy endpoint for backwards compatibility
  app.post('/api/webhooks/contact-form', validateWebhookRequest, async (req: Request, res: Response) => {
    try {
      const validatedData = webhookContactSchema.parse(req.body);
      const submission = await storage.createContactSubmission(validatedData);

      // Dispatch to dynamic webhooks
      await dispatchWebhooks('contact_form', submission);

      res.status(200).json({ 
        success: true, 
        data: submission,
        message: 'Contact submission successfully processed' 
      });

      console.log(`[Webhook] Contact submission processed: ${submission.id}`);
    } catch (error: any) {
      console.error('[Webhook Error] Contact form submission:', error);
      res.status(400).json({ 
        success: false, 
        message: error.message || 'Invalid submission data' 
      });
    }
  });

  app.post('/api/webhooks/chat-message', validateWebhookRequest, async (req: Request, res: Response) => {
    try {
      const validatedData = webhookChatSchema.parse(req.body);
      const message = await storage.createChatMessage(validatedData);

      // Dispatch to dynamic webhooks
      await dispatchWebhooks('chat_message', message);

      res.status(200).json({ 
        success: true, 
        data: message,
        message: 'Chat message successfully processed' 
      });

      console.log(`[Webhook] Chat message processed: ${message.id}`);
    } catch (error: any) {
      console.error('[Webhook Error] Chat message:', error);
      res.status(400).json({ 
        success: false, 
        message: error.message || 'Invalid message data' 
      });
    }
  });

  app.post('/api/webhooks/project-estimate', validateWebhookRequest, async (req: Request, res: Response) => {
    try {
      const validatedData = webhookEstimateSchema.parse(req.body);
      const estimate = await storage.createProjectEstimate(validatedData);

      // Dispatch to dynamic webhooks
      await dispatchWebhooks('project_estimate', estimate);

      res.status(200).json({ 
        success: true, 
        data: estimate,
        message: 'Project estimate successfully processed' 
      });

      console.log(`[Webhook] Project estimate processed: ${estimate.id}`);
    } catch (error: any) {
      console.error('[Webhook Error] Project estimate:', error);
      res.status(400).json({ 
        success: false, 
        message: error.message || 'Invalid estimate data' 
      });
    }
  });

  // Webhook voor content synchronisatie (voor Make/n8n)
  app.post('/api/webhooks/content-sync', validateWebhookRequest, async (req: Request, res: Response) => {
    try {
      const { contentType, action, data } = req.body;

      if (!contentType || !action || !data) {
        return res.status(400).json({
          success: false,
          message: 'Missing required fields: contentType, action, data'
        });
      }

      // Hier zou je code kunnen toevoegen om content te synchroniseren
      // bijv. het aanmaken/bijwerken van content bestanden

      console.log(`[Webhook] Content sync: ${contentType} - ${action}`);

      // Voorbeeld van een succes respons
      res.status(200).json({
        success: true,
        message: `Content ${action} request processed for ${contentType}`
      });
    } catch (error: any) {
      console.error('[Webhook Error] Content sync:', error);
      res.status(500).json({
        success: false,
        message: error.message || 'Error processing content sync webhook'
      });
    }
  });

  // Helper route om webhook configuratie te testen
  app.get('/api/webhooks/test', validateWebhookRequest, (req: Request, res: Response) => {
    res.status(200).json({
      success: true,
      message: 'Webhook authentication successful',
      timestamp: new Date().toISOString()
    });
  });
};

shared/schema.ts.content:
import { pgTable, text, serial, integer, boolean, timestamp, jsonb, varchar } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Users table (keep existing setup)
export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

// Contact form submissions
export const contactSubmissions = pgTable("contact_submissions", {
  id: serial("id").primaryKey(),
  name: text("name").notNull(),
  email: text("email").notNull(),
  company: text("company"),
  projectType: text("project_type"),
  message: text("message").notNull(),
  submittedAt: timestamp("submitted_at").notNull(),
  isProcessed: boolean("is_processed").default(false),
});

export const insertContactSubmissionSchema = createInsertSchema(contactSubmissions).pick({
  name: true,
  email: true,
  company: true,
  projectType: true,
  message: true,
  submittedAt: true,
});

// Chat messages
export const chatMessages = pgTable("chat_messages", {
  id: serial("id").primaryKey(),
  message: text("message").notNull(),
  sender: text("sender").notNull(), // 'user' or 'bot'
  timestamp: timestamp("timestamp").notNull(),
});

export const insertChatMessageSchema = createInsertSchema(chatMessages).pick({
  message: true,
  sender: true,
  timestamp: true,
});

// Project estimates
export const projectEstimates = pgTable("project_estimates", {
  id: serial("id").primaryKey(),
  projectType: text("project_type").notNull(),
  scale: text("scale").notNull(),
  features: jsonb("features").notNull(), // Array of feature IDs
  timelinePriority: integer("timeline_priority").notNull(),
  estimatedBudgetMin: integer("estimated_budget_min").notNull(),
  estimatedBudgetMax: integer("estimated_budget_max").notNull(),
  estimatedTimelineMin: integer("estimated_timeline_min").notNull(),
  estimatedTimelineMax: integer("estimated_timeline_max").notNull(),
  createdAt: timestamp("created_at").notNull(),
});

export const insertProjectEstimateSchema = createInsertSchema(projectEstimates).pick({
  projectType: true,
  scale: true,
  features: true,
  timelinePriority: true,
  estimatedBudgetMin: true,
  estimatedBudgetMax: true,
  estimatedTimelineMin: true,
  estimatedTimelineMax: true,
  createdAt: true,
});

// Type definitions
export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

export type InsertContactSubmission = z.infer<typeof insertContactSubmissionSchema>;
export type ContactSubmission = typeof contactSubmissions.$inferSelect;

export type InsertChatMessage = z.infer<typeof insertChatMessageSchema>;
export type ChatMessage = typeof chatMessages.$inferSelect;

export type InsertProjectEstimate = z.infer<typeof insertProjectEstimateSchema>;
export type ProjectEstimate = typeof projectEstimates.$inferSelect;

// Webhooks table
export const webhooks = pgTable("webhooks", {
  id: serial("id").primaryKey(),
  name: text("name").notNull(),
  url: text("url").notNull(),
  eventType: text("event_type").notNull(), // blog.created, contact.submitted, etc.
  secretToken: text("secret_token"),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").notNull().defaultNow(),
  lastTriggeredAt: timestamp("last_triggered_at"),
  failureCount: integer("failure_count").default(0),
  headers: jsonb("headers").default({}), // Additional headers to send
});

export const insertWebhookSchema = createInsertSchema(webhooks).pick({
  name: true,
  url: true,
  eventType: true,
  secretToken: true,
  isActive: true,
});

export const updateWebhookSchema = createInsertSchema(webhooks).pick({
  name: true,
  url: true,
  eventType: true,
  secretToken: true,
  isActive: true,
  lastTriggeredAt: true,
}).partial();

// API Tokens table
export const apiTokens = pgTable("api_tokens", {
  id: serial("id").primaryKey(),
  name: text("name").notNull(),
  token: varchar("token", { length: 64 }).notNull().unique(),
  createdAt: timestamp("created_at").notNull().defaultNow(),
  expiresAt: timestamp("expires_at"),
  lastUsedAt: timestamp("last_used_at"),
  userId: integer("user_id").notNull().references(() => users.id), // Which user created this token
  scopes: jsonb("scopes").notNull().default(['read']), // Array of permission scopes
});

export const insertApiTokenSchema = createInsertSchema(apiTokens).pick({
  name: true,
  token: true,
  expiresAt: true,
  userId: true,
  scopes: true,
});

// Webhook logs to track delivery history
export const webhookLogs = pgTable("webhook_logs", {
  id: serial("id").primaryKey(),
  webhookId: integer("webhook_id").notNull().references(() => webhooks.id),
  eventType: text("event_type").notNull(),
  payload: jsonb("payload").notNull(),
  responseStatus: integer("response_status"),
  responseBody: text("response_body"),
  error: text("error"),
  duration: integer("duration"), // in milliseconds
  triggeredAt: timestamp("triggered_at").notNull().defaultNow(),
});

export const insertWebhookLogSchema = createInsertSchema(webhookLogs).pick({
  webhookId: true,
  eventType: true,
  payload: true,
  responseStatus: true,
  responseBody: true,
  error: true,
  duration: true,
});

export type InsertWebhook = z.infer<typeof insertWebhookSchema>;
export type Webhook = typeof webhooks.$inferSelect;

export type InsertApiToken = z.infer<typeof insertApiTokenSchema>;
export type ApiToken = typeof apiTokens.$inferSelect;

export type InsertWebhookLog = z.infer<typeof insertWebhookLogSchema>;
export type WebhookLog = typeof webhookLogs.$inferSelect;

// Cookie consent tracking
export const cookieConsents = pgTable("cookie_consents", {
  id: serial("id").primaryKey(),
  sessionId: text("session_id").notNull(),
  ipAddress: text("ip_address"),
  userAgent: text("user_agent"),
  necessary: boolean("necessary").notNull().default(true),
  analytics: boolean("analytics").notNull().default(false),
  marketing: boolean("marketing").notNull().default(false),
  preferences: boolean("preferences").notNull().default(false),
  consentGivenAt: timestamp("consent_given_at").notNull(),
  consentUpdatedAt: timestamp("consent_updated_at"),
  language: text("language").default('nl'),
  pageUrl: text("page_url"),
  referrer: text("referrer"),
});

export const insertCookieConsentSchema = createInsertSchema(cookieConsents).pick({
  sessionId: true,
  ipAddress: true,
  userAgent: true,
  necessary: true,
  analytics: true,
  marketing: true,
  preferences: true,
  consentGivenAt: true,
  language: true,
  pageUrl: true,
  referrer: true,
});

// Cookie analytics events
export const cookieAnalytics = pgTable("cookie_analytics", {
  id: serial("id").primaryKey(),
  sessionId: text("session_id").notNull(),
  eventType: text("event_type").notNull(), // 'banner_shown', 'settings_opened', 'consent_given', 'consent_updated'
  eventData: jsonb("event_data"), // Additional event details
  timestamp: timestamp("timestamp").notNull(),
  pageUrl: text("page_url"),
  userAgent: text("user_agent"),
});

export const insertCookieAnalyticsSchema = createInsertSchema(cookieAnalytics).pick({
  sessionId: true,
  eventType: true,
  eventData: true,
  timestamp: true,
  pageUrl: true,
  userAgent: true,
});

// GDPR data requests
export const gdprRequests = pgTable("gdpr_requests", {
  id: serial("id").primaryKey(),
  requestType: text("request_type").notNull(), // 'access', 'delete', 'portability', 'rectification'
  email: text("email").notNull(),
  firstName: text("first_name"),
  lastName: text("last_name"),
  requestDetails: text("request_details"),
  status: text("status").notNull().default('pending'), // 'pending', 'processing', 'completed', 'rejected'
  submittedAt: timestamp("submitted_at").notNull(),
  processedAt: timestamp("processed_at"),
  processedBy: integer("processed_by"), // References users.id
  responseNotes: text("response_notes"),
  attachments: jsonb("attachments"), // File paths or metadata
});

export const insertGdprRequestSchema = createInsertSchema(gdprRequests).pick({
  requestType: true,
  email: true,
  firstName: true,
  lastName: true,
  requestDetails: true,
  submittedAt: true,
});

export type InsertCookieConsent = z.infer<typeof insertCookieConsentSchema>;
export type CookieConsent = typeof cookieConsents.$inferSelect;

export type InsertCookieAnalytics = z.infer<typeof insertCookieAnalyticsSchema>;
export type CookieAnalytics = typeof cookieAnalytics.$inferSelect;

export type InsertGdprRequest = z.infer<typeof insertGdprRequestSchema>;
export type GdprRequest = typeof gdprRequests.$inferSelect;

