import { Badge } from '@/components/ui/badge';
import { motion } from 'framer-motion';
import { ExternalLink, ArrowRight, Code, Server, Database, Cpu, Cloud } from 'lucide-react';

interface ServiceFeature {
  name: string;
  description: string;
}

interface TechItem {
  name: string;
  description: string;
}

interface Service {
  slug: string;
  title: string;
  icon: string;
  short_description: string;
  featured_image: string;
  order: number;
  features: ServiceFeature[];
  tech_stack: TechItem[];
  content: string;
}

const getIconComponent = (iconName: string) => {
  switch (iconName) {
    case 'chat-bot':
      return <Cpu className="h-6 w-6" />;
    case 'web-dev':
      return <Code className="h-6 w-6" />;
    case 'automation':
      return <Server className="h-6 w-6" />;
    case 'cloud':
      return <Cloud className="h-6 w-6" />;
    default:
      return <Database className="h-6 w-6" />;
  }
};

export function CMSServicesSection() {
  const [services, setServices] = useState<Service[]>([]);
  const [activeTab, setActiveTab] = useState<string>('');
  const [loading, setLoading] = useState(true);
  const { language } = useLanguage();

  useEffect(() => {
    async function fetchServices() {
      try {
        // Add timestamp to prevent caching
        const response = await fetch(`/api/services?lang=${language}&t=${Date.now()}`);
        const data = await response.json();
        if (data.success) {
          const sortedServices = data.data.sort((a: Service, b: Service) => a.order - b.order);
          console.log('Fetched services:', sortedServices.map((s: Service) => s.title));
          setServices(sortedServices);
          if (sortedServices.length > 0) {
            setActiveTab(sortedServices[0].slug);
          }
        }
      } catch (error) {
        console.error('Error fetching services:', error);
      } finally {
        setLoading(false);
      }
    }

    fetchServices();
  }, [language]);

  const container = {
    hidden: { opacity: 0 },
    show: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
      },
    },
  };

  const item = {
    hidden: { opacity: 0, y: 20 },
    show: { opacity: 1, y: 0 },
  };

