import { useEffect, useState } from 'react';
import { Link, useLocation } from 'wouter';
import { useLanguage } from '@/lib/languageContext';
import { X, ChevronDown, ChevronRight } from 'lucide-react';

interface MobileMenuProps {
  isOpen: boolean;
  onClose: () => void;
}

const MobileMenu = ({ isOpen, onClose }: MobileMenuProps) => {
  const { t, language, setLanguage } = useLanguage();
  const [servicesSubMenuOpen, setServicesSubMenuOpen] = useState(false);
  const [location, navigate] = useLocation();

  const closeMobileMenu = () => {
    onClose();
  };

  useEffect(() => {
    const handleEscapeKey = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        closeMobileMenu();
      }
    };

    document.addEventListener('keydown', handleEscapeKey);
    return () => {
      document.removeEventListener('keydown', handleEscapeKey);
    };
  }, []);

  // Prevent body scroll when menu is open
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
      document.body.style.position = 'fixed';
      document.body.style.width = '100%';
      document.body.style.height = '100%';
    } else {
      document.body.style.overflow = '';
      document.body.style.position = '';
      document.body.style.width = '';
      document.body.style.height = '';
    }

    return () => {
      document.body.style.overflow = '';
      document.body.style.position = '';
      document.body.style.width = '';
      document.body.style.height = '';
    };
  }, [isOpen]);

  const handleNavClick = (e: React.MouseEvent<HTMLAnchorElement>) => {
    closeMobileMenu();
  };

  const toggleServicesSubMenu = () => {
    setServicesSubMenuOpen(!servicesSubMenuOpen);
  };

  const scrollToSection = (sectionId: string) => {
    closeMobileMenu();
    // If we're not on the homepage, navigate there first
    if (location !== '/') {
      navigate('/');
      // Wait for navigation to complete, then scroll
      setTimeout(() => {
        const element = document.getElementById(sectionId);
        if (element) {
          element.scrollIntoView({ behavior: 'smooth' });
        }
      }, 100);
    } else {
      // We're already on homepage, just scroll
      const element = document.getElementById(sectionId);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }
  };

  const handleContactClick = () => {
    scrollToSection('contact');
  };

  const handleAboutClick = () => {
    scrollToSection('testimonials');
  };

  return (
    <>
      {/* Backdrop overlay */}
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black/50 md:hidden"
          style={{ 
            zIndex: 9998,
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0
          }}
          onClick={closeMobileMenu}
        />
      )}

      {/* Mobile menu */}
      <div 
        id="mobile-menu" 
        className={`fixed inset-0 bg-primary transform transition-transform duration-300 ease-in-out md:hidden overflow-y-auto ${
          isOpen ? 'translate-x-0' : 'translate-x-full'
        }`}
        style={{ 
          zIndex: 9999,
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          height: '100vh',
          width: '100vw'
        }}
      >
      <div className="flex justify-end p-4">
        <button
          id="close-menu-button"
          className="text-foreground"
          onClick={closeMobileMenu}
          aria-label="Close menu"
        >
          <X className="h-6 w-6" />
        </button>
      </div>
      <nav className="flex flex-col items-center space-y-6 mt-16">
        <div className="flex flex-col items-center">
          <button
            className="flex items-center text-xl text-foreground/80 hover:text-accent transition font-body"
            onClick={toggleServicesSubMenu}
          >
            {t('header.services')} 
            {servicesSubMenuOpen ? 
              <ChevronDown className="ml-1 h-5 w-5" /> : 
              <ChevronRight className="ml-1 h-5 w-5" />
            }
          </button>

          {servicesSubMenuOpen && (
            <div className="mt-3 space-y-4 flex flex-col items-center">
              <Link 
                href="/services/ai-chatbots" 
                className="text-lg text-foreground/80 hover:text-accent transition font-body"
                onClick={closeMobileMenu}
              >
                AI Chatbots
              </Link>
              <Link 
                href="/services/workflow-automation" 
                className="text-lg text-foreground/80 hover:text-accent transition font-body"
                onClick={closeMobileMenu}
              >
                Workflow Automatisering
              </Link>
              <Link 
                href="/services/web-development" 
                className="text-lg text-foreground/80 hover:text-accent transition font-body"
                onClick={closeMobileMenu}
              >
                Webontwikkeling
              </Link>
              <Link 
                href="/services/microsoft-365" 
                className="text-lg text-foreground/80 hover:text-accent transition font-body"
                onClick={closeMobileMenu}
              >
                Microsoft 365
              </Link>
            </div>
          )}
        </div>

        <Link 
          href="/portfolio" 
          className="text-xl text-foreground/80 hover:text-accent transition font-body"
          onClick={closeMobileMenu}
        >
          {t('header.cases')}
        </Link>
        <button 
          onClick={handleAboutClick}
          className="text-xl text-foreground/80 hover:text-accent transition font-body"
        >
          {t('header.about')}
        </button>
        <Link 
          href="/blogs" 
          className="text-xl text-foreground/80 hover:text-accent transition font-body"
          onClick={closeMobileMenu}
        >
          {t('header.blogs')}
        </Link>

        {/* Language switcher */}
        <div className="flex space-x-4 mt-4">
          <button
            onClick={() => setLanguage('nl')}
            className={`px-4 py-2 rounded ${language === 'nl' ? 'bg-accent text-primary' : 'bg-primary/20 text-foreground'}`}
          >
            ðŸ‡³ðŸ‡± NL
          </button>
          <button
            onClick={() => setLanguage('en')}
            className={`px-4 py-2 rounded ${language === 'en' ? 'bg-accent text-primary' : 'bg-primary/20 text-foreground'}`}
          >
            ðŸ‡¬ðŸ‡§ EN
          </button>
        </div>

        <button 
          onClick={handleContactClick}
          className="mt-4 px-6 py-3 rounded-lg bg-accent text-primary font-header font-medium transition hover:bg-accent/90"
        >
          {t('header.contact')}
        </button>
      </nav>
      </div>
    </>
  );
};

export default MobileMenu;

client/src/components/MonitorFrame.tsx.content:
import React, { useState, useEffect } from 'react';

interface MonitorFrameProps {
  imageUrl: string;
  altText: string;
  websiteUrl?: string;
  className?: string;
  isLoading?: boolean;
}

const MonitorFrame: React.FC<MonitorFrameProps> = ({ 
  imageUrl, 
  altText, 
  websiteUrl,
  className = '',
  isLoading = false
}) => {
  // Gebruik direct de fallback als startpunt
  const fallbackImage = '/images/portfolio/fasttaxi.png';
  const [imageSrc, setImageSrc] = useState<string>(fallbackImage);
  const [imageError, setImageError] = useState<boolean>(false);

  // Probeer de afbeelding te laden bij initialisatie of wanneer imageUrl wijzigt
  useEffect(() => {
    // Reset error state wanneer imageUrl verandert
    setImageError(false);

    // Gebruik direct de imageUrl (indien beschikbaar) of fallback
    if (imageUrl) {
      setImageSrc(imageUrl);
    } else {
      setImageSrc(fallbackImage);
    }
  }, [imageUrl]);

  return (
    <div className={`relative ${className}`}>
      {isLoading ? (
        <div className="absolute inset-0 flex items-center justify-center bg-black/10 rounded-md">
          <div className="animate-spin w-8 h-8 border-4 border-accent border-t-transparent rounded-full"></div>
        </div>
      ) : (
        <>
          {/* Monitor frame */}
          <div className="relative w-full">
            {/* Monitor top bar with browser controls */}
            <div className="bg-gray-900 rounded-t-xl p-2 flex items-center">
              <div className="flex space-x-1.5">
                <div className="w-3 h-3 rounded-full bg-red-500"></div>
                <div className="w-3 h-3 rounded-full bg-yellow-500"></div>
                <div className="w-3 h-3 rounded-full bg-green-500"></div>
              </div>
              {websiteUrl && (
                <div className="mx-auto text-center text-xs text-gray-400 font-mono overflow-hidden text-ellipsis whitespace-nowrap max-w-[180px] md:max-w-full">
                  {websiteUrl}
                </div>
              )}
            </div>

            {/* Monitor screen content */}
            <div className="bg-white border-l-2 border-r-2 border-gray-900 relative overflow-hidden">
              <div className="absolute inset-0 bg-gray-100 flex items-center justify-center">
                <div className="w-full h-full overflow-hidden bg-white">
                  <img 
                    src={imageSrc} 
                    alt={altText} 
                    className="w-full h-full object-contain object-center"
                    onError={(e) => {
                      console.error("Afbeelding kon niet worden geladen:", imageSrc);
                      setImageError(true);
                      e.currentTarget.src = fallbackImage;
                    }}
                  />
                </div>
              </div>

              {/* Screen reflection overlay */}
              <div className="absolute inset-0 bg-gradient-to-br from-white/5 to-transparent pointer-events-none"></div>
            </div>

            {/* Monitor base */}
            <div className="bg-gray-900 h-3 rounded-b-md"></div>
            <div className="bg-gray-800 h-2 w-32 mx-auto rounded-b-md"></div>
            <div className="h-10 w-24 mx-auto border-b-[10px] border-l-[7px] border-r-[7px] border-transparent border-t-0 border-gray-800 rounded-b-lg"></div>
          </div>
        </>
      )}
    </div>
  );
};

export default MonitorFrame;

client/src/components/N8nChatWidget.tsx.content:
import { useEffect } from 'react';
import '@n8n/chat/style.css';
import { createChat } from '@n8n/chat';
import mascotImage from '@assets/mascot_1750294453535.png';

export function N8nChatWidget() {
  useEffect(() => {
    // Add target element for n8n chat
    const chatContainer = document.createElement('div');
    chatContainer.id = 'n8n-chat';
    document.body.appendChild(chatContainer);

    // Initialize n8n chat widget
    createChat({
      webhookUrl: 'https://dcwerk.app.n8n.cloud/webhook/2261c842-887d-4a32-8ac7-ff81ae696e5f/chat',
      target: '#n8n-chat',
      mode: 'window',
      showWelcomeScreen: false,
      defaultLanguage: 'en',
      initialMessages: [
        'Welkom bij Digimaatwerk! ðŸ‘‹',
        'Ik ben Maatje, uw digitale assistent. Hoe kan ik u vandaag helpen met uw digitale transformatie?'
      ],
      i18n: {
        en: {
          title: 'Maatje',
          subtitle: 'Uw partner in digitale transformatie',
          footer: 'Powered by Maatje',
          getStarted: 'Start gesprek',
          inputPlaceholder: 'Stel uw vraag over onze diensten...',
          closeButtonTooltip: 'Chat sluiten',
        },
        nl: {
          title: 'Maatje',
          subtitle: 'Uw partner in digitale transformatie',
          footer: 'Powered by Maatje',
          getStarted: 'Start gesprek',
          inputPlaceholder: 'Stel uw vraag over onze diensten...',
          closeButtonTooltip: 'Chat sluiten',
        },
      },
    });

    // Add CSS to replace all images with Maatje logo
    const style = document.createElement('style');
    style.textContent = `
      /* Replace ALL images in chat widget with Maatje logo */
      .n8n-chat img:not(.maatje-logo) {
        content: url('${mascotImage}') !important;
        width: 40px !important;
        height: 40px !important;
        border-radius: 50% !important;
        object-fit: cover !important;
      }

      /* Replace toggle button with Maatje */
      .n8n-chat button[class*="toggle"],
      .n8n-chat [class*="toggle"] {
        background-image: url('${mascotImage}') !important;
        background-size: cover !important;
        background-position: center !important;
        background-repeat: no-repeat !important;
        border-radius: 50% !important;
        width: 60px !important;
        height: 60px !important;
      }

      /* Remove hover effects for toggle button */
      .n8n-chat button[class*="toggle"]:hover,
      .n8n-chat [class*="toggle"]:hover {
        transform: none !important;
        box-shadow: none !important;
        background-image: url('${mascotImage}') !important;
        background-size: cover !important;
        background-position: center !important;
      }

      /* Hide original toggle button content */
      .n8n-chat button[class*="toggle"] svg,
      .n8n-chat [class*="toggle"] svg,
      .n8n-chat button[class*="toggle"] img,
      .n8n-chat [class*="toggle"] img {
        display: none !important;
      }

      /* Style the custom Maatje logo */
      .n8n-chat .maatje-logo {
        width: 40px !important;
        height: 40px !important;
        border-radius: 50% !important;
        object-fit: cover !important;
        margin-right: 8px !important;
        display: block !important;
        visibility: visible !important;
        opacity: 1 !important;
        z-index: 1000 !important;
      }
    `;
    document.head.appendChild(style);

    // Function to replace all images with Maatje and style toggle button
    const manageChatLogos = () => {
      const chatWidget = document.querySelector('#n8n-chat');
      if (chatWidget) {
        // Replace all images with Maatje logo
        const allImages = chatWidget.querySelectorAll('img:not(.maatje-logo)');
        allImages.forEach(img => {
          (img as HTMLImageElement).src = mascotImage;
          (img as HTMLImageElement).alt = 'Maatje';
          (img as HTMLImageElement).style.borderRadius = '50%';
          (img as HTMLImageElement).style.objectFit = 'cover';
          (img as HTMLImageElement).style.width = '40px';
          (img as HTMLImageElement).style.height = '40px';
        });

        // Style toggle button with Maatje background (no hover effects)
        const toggleButtons = chatWidget.querySelectorAll('button[class*="toggle"], [class*="toggle"]');
        toggleButtons.forEach(btn => {
          const button = btn as HTMLElement;
          button.style.backgroundImage = `url('${mascotImage}')`;
          button.style.backgroundSize = 'cover';
          button.style.backgroundPosition = 'center';
          button.style.borderRadius = '50%';
          button.style.width = '60px';
          button.style.height = '60px';
        });
      }
    };

    // Add observer to manage logos when chat widget loads
    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (mutation.type === 'childList') {
          manageChatLogos();
        }
      });
    });

    // Check once after a delay to ensure widget is loaded
    setTimeout(() => {
      manageChatLogos();
    }, 2000);

    // Start observing
    observer.observe(document.body, {
      childList: true,
      subtree: true
    });

    // Cleanup function
    return () => {
      const existingContainer = document.getElementById('n8n-chat');
      if (existingContainer) {
        existingContainer.remove();
      }
      // Remove the custom style element
      if (style && style.parentNode) {
        style.parentNode.removeChild(style);
      }
      // Disconnect the observer
      observer.disconnect();
    };
  }, []);

  return null; // The chat widget is handled by n8n's createChat function
}

